import test from 'ava';
import CypherQuery from '../index';

test('Match clause', t => {
	t.is(new CypherQuery().match().queryString, 'MATCH ')
	t.is(new CypherQuery({debug: true}).match().queryString, 'MATCH ')
	t.is(new CypherQuery({debug: true}).match('custom string').queryString, 'MATCH custom string \n')
	t.is(new CypherQuery().match('pattern1', 'pattern2').queryString, 'MATCH pattern1, pattern2 ')
});